import React, { useState, useCallback, useRef } from "react";
import { Toast } from "primereact/toast";
import { FormFeld } from "../../services/FormFeld";
import { buttonNeuerVerein } from "./ButtonNeuerVerein";
import { Verein } from "../interfaces/Verein";

interface VereinFormViewProps {
  onNeuerVerein: () => boolean;
  btnNeuerVerein: boolean;
  onÄndernVerein: () => boolean;
  btnÄndernVerein: boolean;
  onDeleteVerein: () => boolean;
  btnLöschenVerein: boolean;
  onStartMenue: () => boolean;
  selectedVerein: Verein | null;
}

export const VereinFormView2: React.FC<VereinFormViewProps> = ({
  onNeuerVerein,
  btnNeuerVerein,
  onÄndernVerein,
  btnÄndernVerein,
  onDeleteVerein,
  btnLöschenVerein,
  onStartMenue,
  selectedVerein,
}) => {
  const toast = useRef<Toast>(null);
  const [visible, setVisible] = useState(false);

  const accept = useCallback(() => {
    if (selectedVerein) {
      const message = selectedVerein.name + " wurde gelöscht!";
      onDeleteVerein();
      if (toast.current) {
        toast.current.show({
          severity: "info",
          summary: "Bestätigung",
          detail: message,
          life: 3000,
        });
      }
    }
  }, [selectedVerein, onDeleteVerein]);

  const reject = useCallback(() => {
    if (selectedVerein) {
      const message = selectedVerein.name + " wurde nicht gelöscht!";
      if (toast.current) {
        toast.current.show({
          severity: "warn",
          summary: "Abbruch",
          detail: message,
          life: 3000,
        });
      }
    }
  }, [selectedVerein]);

  return (
    <>
      <Toast ref={toast} />
      <div className="grid m-auto w-11">
        {selectedVerein && (
          <>
            {FormFeld(selectedVerein.abk, "Kurzname", true, (value) => false)}
            {FormFeld(selectedVerein.name, "Verein", true, (value) => false)}
            {FormFeld(selectedVerein.strasse, "Strasse", true, (value) => false)}
            {FormFeld(selectedVerein.plz, "PLZ", true, (value) => false)}
            {FormFeld(selectedVerein.ort, "Ort", true, (value) => false)}
            {FormFeld(selectedVerein.bankName, "Bank", true, (value) => false)}
            {FormFeld(selectedVerein.kontoInhaber, "Kontoinhaber", true, {})}
            {FormFeld(selectedVerein.kiAnschrift, "Anschrift", true, {})}
            {FormFeld(selectedVerein.iban, "IBAN", true, {})}
            {FormFeld(selectedVerein.bic, "BIC", true, {})}
          </>
        )}
      </div>
      {buttonNeuerVerein({
        onNeuerVerein,
        btnNeuerVerein,
        visible,
        setVisible,
        selectedVerein,
        accept,
        reject,
        onÄndernVerein,
        btnÄndernVerein,
        btnLöschenVerein,
        onStartMenue,
      })}
    </>
  );
};
