const baseURL = 'http://localhost:8080/api/books';

const getCollectionURL = () => baseURL;

const getElementURL = vereinId =>
  `${getCollectionURL()}/${encodeURIComponent(vereinId)}`;

export const getAllVereine = async () => {

  const res = await fetch(getCollectionURL());
  const vereine = await res.json();
  // return vereine;

  return new Promise(resolve =>
    setTimeout(() =>
      resolve(vereine), 500));
};

export const createVerein = async (verein) => {
  const res = await fetch(getCollectionURL(), {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(verein),
  });
  const savedVerein = await res.json();
  return savedVerein;
};

export const replaceVerein = async (verein) => {
  const res = await fetch(getElementURL(verein.id), {
    method: 'PUT',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(verein),
  });
  const savedVerein = await res.json();
  return savedVerein;
};

export const deleteVerein = async (vereinId) => {
  await fetch(getElementURL(vereinId), {
    method: 'DELETE',
  });
};
