import { BtnStoreCancel } from "./../../services/BtnStoreCancel";
import { FormFeld } from "../../services/FormFeld";

import React, { useState, useRef, useCallback } from "react";

import { Toast } from "primereact/toast";

export function VereinEditForm({
  modusNeuerVerein,
  onSpeichern,
  onAbbruch,
  verein,
}) {
  const toast = useRef(null);

  const club = verein;

/*   const createUpdateVerein = useCallback(() => {
    if (name.length === 0 || abk.length === 0) {
      toast.current.show({
        severity: "warn",
        summary: "Achtung",
        detail:
          "mindestens Vereinsname und Kurzbezeichnung müssen eingegeben werden!",
        life: 3000,
      });
    } else {
      const neuerVerein = {
        name: name,
        abk: abk,
        strasse: strasse,
        plz: plz,
        ort: ort,
        telefon: telefon,
        bank_name: bankName,
        kontoInhaber: kontoInhaber,
        kiAnschrift: kiAnschrift,
        iban: iban,
        bic: bic,
      };
      console.log(neuerVerein);
      onSpeichern(neuerVerein);
    }
  }); */

  let vereinsNameTemp = "";
  let vereinsKurzTemp = "";
  let vereinsStrasseTemp = "";
  let vereinsPLZTemp = "";
  let vereinsOrtTemp = "";
  let vereinsTelefonTemp = "";
  let vereinsBankTemp = "";
  let vereinsKontoInhaberTemp = "";
  let vereinsKontoInhaberAdresseTemp = "";
  let vereinsIBANTemp = "";
  let vereinsBICTemp = "";

  if (!modusNeuerVerein) {
    extractVerein2();
  }

  const [name, setVereinsName] = useState(vereinsNameTemp);
  const [abk, setVereinsKurz] = useState(vereinsKurzTemp);
  const [strasse, setVereinsStrasse] = useState(vereinsStrasseTemp);
  const [plz, setVereinsPLZ] = useState(vereinsPLZTemp);
  const [ort, setVereinsOrt] = useState(vereinsOrtTemp);
  const [telefon, setVereinsTelefon] = useState(vereinsTelefonTemp);
  const [bankName, setVereinsBank] = useState(vereinsBankTemp);
  const [kontoInhaber, setVereinsKontoInhaber] = useState(
    vereinsKontoInhaberTemp
  );
  const [kiAnschrift, setVereinsKontoInhaberAdresse] = useState(
    vereinsKontoInhaberAdresseTemp
  );
  const [iban, setVereinsIBAN] = useState(vereinsIBANTemp);
  const [bic, setVereinsBIC] = useState(vereinsBICTemp);

  return (
    <>
      <Toast ref={toast} />
      <div className="grid m-auto w-11">
        {FormFeld(abk, "Abkürzung", false, setVereinsKurz)}
        {FormFeld(name, "Vereinsname", false, setVereinsName)}
        {FormFeld(strasse, "Strasse", false, setVereinsStrasse)}
        {FormFeld(plz, "PLZ", false, setVereinsPLZ)}
        {FormFeld(ort, "Ort", false, setVereinsOrt)}
        {FormFeld(telefon, "Telefon", false, setVereinsTelefon)}
        {FormFeld(bankName, "Bank", false, setVereinsBank)}
        {FormFeld(kontoInhaber, "Kontoinhaber", false, setVereinsKontoInhaber)}
        {FormFeld(
          kiAnschrift,
          "Kontoinhaber Adresse",
          false,
          setVereinsKontoInhaberAdresse
        )}
        {FormFeld(iban, "IBAN", false, setVereinsIBAN)}
        {FormFeld(bic, "BIC", false, setVereinsBIC)}
      </div>
      <BtnStoreCancel
        createUpdate={createUpdateVerein}
        onAbbruch={onAbbruch}
      />
    </>
  );

  function extractVerein2() {
    for (const property in club) {
      switch (property) {
        case "verein_name":
          vereinsNameTemp = club[property];
          break;
        case "verein_kurz":
          vereinsKurzTemp = club[property];
          break;
        case "verein_strasse":
          vereinsStrasseTemp = club[property];
          break;
        case "verein_plz":
          vereinsPLZTemp = club[property];
          break;
        case "verein_ort":
          vereinsOrtTemp = club[property];
          break;
        case "verein_telefon_privat":
          vereinsTelefonTemp = club[property];
          break;
        case "verein_bankname":
          vereinsBankTemp = club[property];
          break;
        case "verein_konto_inhaber":
          vereinsKontoInhaberTemp = club[property];
          break;
        case "verein_ki_anschrift":
          vereinsKontoInhaberAdresseTemp = club[property];
          break;
        case "verein_iban":
          vereinsIBANTemp = club[property];
          break;
        case "verein_bic":
          vereinsBICTemp = club[property];
          break;
        default:
          //console.log("Feld nicht zugewiesen");
          break;
      }
    }
  }
}
