import React, { useState, useRef, useCallback } from "react";
import { Button } from "primereact/button";
import { ConfirmDialog } from "primereact/confirmdialog";
import { Toast } from "primereact/toast";
import { FormFeld } from "../../services/FormFeld";
import { ExtractPerson } from "./ExtractPerson";
import { BtnEditDeleteBack } from "../../services/BtnEditDeleteBack";

export function MitgliedViewForm({
  onNeuePerson,
  btnNeuePerson,
  onÄndernPerson,
  btnÄndernPerson,
  onDeletePerson,
  btnLöschenPerson,
  onStartMenue,
  person,
}) {
  let personName = "";
  let personVorname = "";
  let personGebDat = "";
  let personSex = "";
  let personStrasse = "";
  let personPLZ = "";
  let personOrt = "";
  let personTelFestnetz = "";
  let personTelMobil = "";
  let personTelEmail = "";
  let personStatus = "";
  let personStatusDatum = "";
  let personBank = "";
  let personIBAN = "";
  let personBIC = "";

  const member = person[0];

  const toast = useRef(null);
  const [visible, setVisible] = useState(false);

  const accept = useCallback(() => {
    onDeletePerson();
    const message = personVorname + " " + personName + " wurde gelöscht!";
    toast.current.show({
      severity: "info",
      summary: "Bestätigung",
      detail: message,
      life: 3000,
    });
  });

  const reject = useCallback(() => {
    const message = personVorname + " " + personName + " wurde nicht gelöscht!";
    toast.current.show({
      severity: "warn",
      summary: "Abbruch",
      detail: message,
      life: 3000,
    });
  });

  ({
    personName,
    personVorname,
    personGebDat,
    personSex,
    personStrasse,
    personPLZ,
    personOrt,
    personTelFestnetz,
    personTelMobil,
    personTelEmail,
    personStatus,
    personStatusDatum,
    personBank,
    personIBAN,
    personBIC,
  } = ExtractPerson(
    member,
    personName,
    personVorname,
    personGebDat,
    personSex,
    personStrasse,
    personPLZ,
    personOrt,
    personTelFestnetz,
    personTelMobil,
    personTelEmail,
    personStatus,
    personStatusDatum,
    personBank,
    personIBAN,
    personBIC
  ));
  return (
    <>
      <Toast ref={toast} />
      <div className="grid m-auto w-11">
        {FormFeld(personName, "Name", true)}
        {FormFeld(personVorname, "Vorname", true)}
        {FormFeld(personGebDat, "Geburtsdatum", true)}
        {FormFeld(personSex, "Sex", true)}
        {FormFeld(personStrasse, "Strasse", true)}
        {FormFeld(personPLZ, "PLZ", true)}
        {FormFeld(personOrt, "Ort", true)}
        {FormFeld(personTelFestnetz, "Telefon", true)}
        {FormFeld(personTelMobil, "Mobil", true)}
        {FormFeld(personTelEmail, "E-Mail", true)}
        {FormFeld(personStatus, "Status", true)}
        {FormFeld(personStatusDatum, "S-Datum", true)}
        {FormFeld(personBank, "Bank", true)}
        {FormFeld(personIBAN, "IBAN", true)}
        {FormFeld(personBIC, "BIC", true)}
      </div>
      <Button
        label="Neue Person"
        className="p-button-outlined m-2"
        onClick={onNeuePerson}
        disabled={btnNeuePerson}
      />

      <ConfirmDialog
        visible={visible}
        onHide={() => setVisible(false)}
        message={
          "Soll " + personVorname + " " + personName + " gelöscht werden?"
        }
        header={"Löschen von " + personVorname + " " + personName + "?"}
        icon="pi pi-exclamation-triangle"
        accept={accept}
        reject={reject}
      />
      <BtnEditDeleteBack
        onÄndern={onÄndernPerson}
        btnÄndern={btnÄndernPerson}
        setVisible={setVisible}
        btnLöschen={btnLöschenPerson}
        onStartMenue={onStartMenue}
      />
    </>
  );
}
