import React, { useState, useRef, useCallback, useEffect } from "react";
import { Toast } from "primereact/toast";
import { BtnStoreCancel } from "../../services/BtnStoreCancel";
import { FormFeld } from "../../services/FormFeld";
import { Person } from "../interfaces/Person";

interface MitgliederEditFormProps {
  onSpeichern: (mitglied: Person) => void;
  onAbbruch: () => void;
  mitglied?: Person;
}

export function MitgliederEditForm({
  onSpeichern,
  onAbbruch,
  verein,
}: MitgliederEditFormProps) {
  const [name, setMitgliederName] = useState("");
  const [abk, setMitgliederKurz] = useState("");
  const [strasse, setMitgliederStrasse] = useState("");
  const [plz, setMitgliederPLZ] = useState("");
  const [ort, setMitgliederOrt] = useState("");
  const [telefon, setMitgliederTelefon] = useState("");
  const [bankName, setMitgliederBankName] = useState("");
  const [kontoInhaber, setMitgliederKontoInhaber] = useState("");
  const [kiAnschrift, setMitgliederKIAnschrift] = useState("");
  const [iban, setMitgliederIBAN] = useState("");
  const [bic, setMitgliederBIC] = useState("");
  const toast = useRef<Toast | null>(null);

  // console.log(modusNeuerVerein, verein);

  const createUpdateVerein = useCallback(() => {
    // If verein exists, it´s an update, so include the "id" property
    if (verein) {
      const updatedVerein: Verein = {
        id: verein.id, // Include the id
        name,
        abk,
        strasse,
        plz,
        ort,
        telefon,
        bankName,
        kontoInhaber,
        kiAnschrift,
        iban,
        bic,
      };
      onSpeichern(updatedVerein);
    } else {
      // If verein doesn't exist, it's a new entry, you might handle this case differently
      // For example, create a new instance or show an error//
    }
  }, [
    name,
    abk,
    strasse,
    plz,
    ort,
    telefon,
    bankName,
    kontoInhaber,
    kiAnschrift,
    iban,
    bic,
    onSpeichern,
  ]);

  useEffect(() => {
    if (verein) {
      setMitgliederName(verein.name);
      setMitgliederKurz(verein.abk);
      setMitgliederStrasse(verein.strasse);
      setMitgliederPLZ(verein.plz);
      setMitgliederOrt(verein.ort);
      setMitgliederTelefon(verein.telefon);
      setMitgliederBankName(verein.bankName);
      setMitgliederKontoInhaber(verein.kontoInhaber);
      setMitgliederKIAnschrift(verein.kiAnschrift);
      setMitgliederIBAN(verein.iban);
      setMitgliederBIC(verein.bic);
    }
  }, [verein]);

  const handleCreateUpdateVerein = () => {
    createUpdateVerein();
    return false; // Placeholder value indicating that the update was not successful
  };

  return (
    <>
      <Toast ref={toast} />
      <div className="grid m-auto w-11">
        {FormFeld(abk, "Abkürzung", false, setVereinsKurz)}
        {FormFeld(name, "Vereinsname", false, setVereinsName)}
        {FormFeld(strasse, "Strasse", false, setMitgliederStrasse)}
        {FormFeld(plz, "PLZ", false, setMitgliederPLZ)}
        {FormFeld(ort, "Ort", false, setMitgliederOrt)}
        {FormFeld(telefon, "Telefon", false, setMitgliederTelefon)}
        {FormFeld(bankName, "Bank", false, setMitgliederBankName)}
        {FormFeld(kontoInhaber, "Konto-Inhaber", false, setMitgliederKontoInhaber)}
        {FormFeld(kiAnschrift, "Anschrift", false, setMitgliederKIAnschrift)}
        {FormFeld(iban, "IBAN", false, setMitgliederIBAN)}
        {FormFeld(bic, "BIC", false, setMitgliederBIC)}
      </div>
      <BtnStoreCancel
        createUpdate={handleCreateUpdateVerein}
        onAbbruch={onAbbruch}
      />
    </>
  );
}
