import React from "react";


// loadingOrErrorUtils.js
export const renderLoadingOrError = ({ loading, error }) => {
    if (loading) {
      return <div>Loading...</div>;
    }
    if (error) {
      return <div>Error: {error}</div>;
    }
    return null;
  };
