import { BtnEditDeleteBack } from "../../services/BtnEditDeleteBack";
import React, { useState, useRef, useCallback } from "react";
import { Button } from "primereact/button";
import { ConfirmDialog } from "primereact/confirmdialog";
import { Toast } from "primereact/toast";
import { FormFeld } from "../../services/FormFeld";
import { ExtractVerein } from "./ExtractVerein";

export function VereinViewForm({
  onNeuerVerein, // Neuer Verein
  btnNeuerVerein, // Button Neuer Verein aktiv?
  onÄndernVerein,
  btnÄndernVerein,
  onDeleteVerein,
  btnLöschenVerein,
  onStartMenue,
  verein,
}) {
  let vereinsName = "";
  let vereinsKurz = "";
  let vereinsStrasse = "";
  let vereinsPLZ = "";
  let vereinsOrt = "";
  let vereinsTelefon = "";
  let vereinsBank = "";
  let vereinsKontoInhaber = "";
  let vereinsKontoInhaberAdresse = "";
  let vereinsIBAN = "";
  let vereinsBIC = "";

  const club = verein[0];

  const toast = useRef(null);
  const [visible, setVisible] = useState(false);

  const accept = useCallback(() => {
    const message = vereinsName + " wurde gelöscht!";
    onDeleteVerein();
    toast.current.show({
      severity: "info",
      summary: "Bestätigung",
      detail: message,
      life: 3000,
    });
  });

  const reject = useCallback(() => {
    const message = vereinsName + " wurde nicht gelöscht!";
    toast.current.show({
      severity: "warn",
      summary: "Abbruch",
      detail: message,
      life: 3000,
    });
  });

  ({
    vereinsName,
    vereinsKurz,
    vereinsStrasse,
    vereinsPLZ,
    vereinsOrt,
    vereinsTelefon,
    vereinsBank,
    vereinsKontoInhaber,
    vereinsKontoInhaberAdresse,
    vereinsIBAN,
    vereinsBIC,
  } = ExtractVerein(
    club,
    vereinsName,
    vereinsKurz,
    vereinsStrasse,
    vereinsPLZ,
    vereinsOrt,
    vereinsTelefon,
    vereinsBank,
    vereinsKontoInhaber,
    vereinsKontoInhaberAdresse,
    vereinsIBAN,
    vereinsBIC
  ));

  return (
    <>
      <Toast ref={toast} />
      <div className="grid m-auto w-11">
        {FormFeld(vereinsName, "Vereinsname", true)}
        {FormFeld(vereinsKurz, "Kurzname", true)}
        {FormFeld(vereinsStrasse, "Strasse", true)}
        {FormFeld(vereinsPLZ, "PLZ", true)}
        {FormFeld(vereinsOrt, "Ort", true)}
        {FormFeld(vereinsTelefon, "Telefon", true)}
        {FormFeld(vereinsBank, "Bank", true)}
        {FormFeld(vereinsKontoInhaber, "Kontoinhaber", true)}
        {FormFeld(vereinsKontoInhaberAdresse, "Kontoinhaber-Adresse", true)}
        {FormFeld(vereinsIBAN, "IBAN", true)}
        {FormFeld(vereinsBIC, "BIC", true)}
      </div>
      <Button
        label="Neuer Verein"
        className="p-button-outlined m-2"
        onClick={onNeuerVerein}
        disabled={btnNeuerVerein}
      />
      <ConfirmDialog
        visible={visible}
        onHide={() => setVisible(false)}
        message={"Soll der " + vereinsName + " gelöscht werden?"}
        header={"Löschen des " + vereinsName + "?"}
        icon="pi pi-exclamation-triangle"
        accept={accept}
        reject={reject}
      />
      <BtnEditDeleteBack
        onÄndern={onÄndernVerein}
        btnÄndernVerein={btnÄndernVerein}
        setVisible={setVisible}
        btnLöschen={btnLöschenVerein}
        onStartMenue={onStartMenue}
      />
    </>
  );
}
