import React, { useState, useCallback, useRef } from "react";
import { Toast } from "primereact/toast";
import { BtnEditDeleteBack } from "../../services/BtnEditDeleteBack";
import { FormFeld } from "../../services/FormFeld";
import { Button } from "primereact/button";
import { ConfirmDialog } from "primereact/confirmdialog";

export const VereinFormView2 = ({
  onNeuerVerein, // Neuer Verein
  btnNeuerVerein, // Button Neuer Verein aktiv?
  onÄndernVerein,
  btnÄndernVerein,
  onDeleteVerein,
  btnLöschenVerein,
  onStartMenue,
  selectedVerein,
}) => {
  const toast = useRef(null);
  const [visible, setVisible] = useState(false);

  const accept = useCallback(() => {
    const message = selectedVerein.name + " wurde gelöscht!";
    onDeleteVerein();
    toast.current.show({
      severity: "info",
      summary: "Bestätigung",
      detail: message,
      life: 3000,
    });
  });

  const reject = useCallback(() => {
    const message = selectedVerein.name + " wurde nicht gelöscht!";
    toast.current.show({
      severity: "warn",
      summary: "Abbruch",
      detail: message,
      life: 3000,
    });
  });

  if (selectedVerein) {
      const newLocal = <div>
          <Button
              label="Neuer Verein"
              className="p-button-outlined m-2"
              onClick={onNeuerVerein}
              disabled={btnNeuerVerein} />
          <ConfirmDialog
              visible={visible}
              onHide={() => setVisible(false)}
              message={"Soll der " + selectedVerein.name + " gelöscht werden?"}
              header={"Löschen des " + selectedVerein.name + "?"}
              icon="pi pi-exclamation-triangle"
              accept={accept}
              reject={reject} />
          <BtnEditDeleteBack
              onÄndern={onÄndernVerein}
              btnÄndernVerein={btnÄndernVerein}
              setVisible={setVisible}
              btnLöschen={btnLöschenVerein}
              onStartMenue={onStartMenue} />
      </div>;
    return (
      <>
        <Toast ref={toast} />
        <div className="grid m-auto w-11">
          {FormFeld(selectedVerein.name, "Vereinsname", true)}
          {FormFeld(selectedVerein.abk, "Kurzname", true)}
          {FormFeld(selectedVerein.strasse, "Strasse", true)}
          {FormFeld(selectedVerein.plz, "PLZ", true)}
          {FormFeld(selectedVerein.ort, "Ort", true)}
          {FormFeld(selectedVerein.telefon, "Telefon", true)}
          {FormFeld(selectedVerein.bankName, "Bank", true)}
          {FormFeld(selectedVerein.kontoInhaber, "Kontoinhaber", true)}
          {FormFeld(selectedVerein.kiAnschrift, "Kontoinhaber-Adresse", true)}
          {FormFeld(selectedVerein.iban, "IBAN", true)}
          {FormFeld(selectedVerein.bic, "BIC", true)}
        </div>
        {newLocal}
      </>
    );
  } else if (btnNeuerVerein) {
    // Display the same form but for a new Verein
    return (
      <>
        <Toast ref={toast} />
        <div className="grid m-auto w-11">
          {FormFeld("", "Vereinsname", false, (value) =>
            this.onNeuerVereinFieldChange("name", value)
          )}
          {FormFeld("", "Kurzname", false, (value) =>
            this.onNeuerVereinFieldChange("abk", value)
          )}
          {FormFeld("", "Strasse", false, (value) =>
            this.onNeuerVereinFieldChange("strasse", value)
          )}

          {/* ... (other fields) */}
        </div>
        <Button
          label="Neuer Verein"
          className="p-button-outlined m-2"
          onClick={this.createNewVerein}
        />
        {/* ... (other buttons) */}
      </>
    );
  }
  return null;
};
