// const baseURL = 'http://localhost:3050/personen';
const baseURL = 'http://localhost:8080/api/persons';

const getCollectionURL = () => baseURL;

const getElementURL = personId =>
  `${getCollectionURL()}/${encodeURIComponent(personId)}`;

export const getAllPersonen = async () => {

  const res = await fetch(getCollectionURL());
  const personen = await res.json();
  // return personen;

  return new Promise(resolve =>
    setTimeout(() =>
      resolve(personen), 500));
};

export const createPerson = async (person) => {
  const res = await fetch(getCollectionURL(), {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(person),
  });
  const savedPerson = await res.json();
  return savedPerson;
};

export const replacePerson = async (person) => {
  const res = await fetch(getElementURL(person.id), {
    method: 'PUT',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(person),
  });
  const savedPerson = await res.json();
  return savedPerson;
};

export const deletePerson = async (personId) => {
  await fetch(getElementURL(personId), {
    method: 'DELETE',
  });
};
