import React from "react";
import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { DataTableSelectParams } from "primereact/datatable";
import { Person } from "../interfaces/Person";

interface PersonTableProps {
  data: Person[];
  selectedPerson: Person | null; // Use `null` as initial selection value
  handleRowSelect: (e: DataTableSelectParams) => void;
}

export const PersonTable: React.FC<PersonTableProps> = ({
  data,
  selectedPerson,
  handleRowSelect,
}) => {
  return (
    <div className="card border-solid m-auto w-11">
      <DataTable
        value={data}
        size="small"
        selectionMode="single"
        selection={selectedPerson}
        sortField="name"
        sortOrder={1}
        scrollable
        scrollHeight="35vh"
        onRowSelect={handleRowSelect}
      >
        {/* ... Columns ... */}
        <Column
          field="vorname"
          header="Vorname"
          style={{
            minWidth: "30px",
          }}
          sortable
        ></Column>
        <Column
          field="name"
          header="Name"
          style={{
            minWidth: "200px",
          }}
          sortable
        ></Column>
        <Column
          field="strasse"
          header="Strasse"
          style={{
            minWidth: "100px",
          }}
        ></Column>
        <Column
          field="ort"
          header="Ort"
          style={{
            minWidth: "100px",
          }}
          sortable
        ></Column>
      </DataTable>
    </div>
  );
};
