import React, { Component } from "react";
import axios from "axios";
import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";

import { MenueHeader } from "../MenueHeader";

class Vereine2 extends Component {
  state = {
    data: [],
    selectedVerein: null,
    loading: true,
    error: null,
  };

  componentDidMount() {
    axios
      .get("http://localhost:8080/verein") // Replace with your Spring Boot API endpoint
      .then((response) => {
        this.setState({
          data: response.data,
          loading: false,
          error: null,
        });
      })
      .catch((error) => {
        this.setState({
          data: [],
          loading: false,
          error: "An error occurred while fetching data.",
        });
      });
  }

  handleRowHover = (event) => {
    this.setState({ selectedVerein: event.data });
  };

  render() {
    const { data, selectedVerein, loading, error } = this.state;

    if (loading) {
      return <div>Loading...</div>;
    }

    if (error) {
      return <div>Error: {error}</div>;
    }

    const vereinAnz = data.length; // Anzahl der Vereine

    return (
      <div className="border-solid m-auto w-11">
        <div className="card">
          <MenueHeader headerText={vereinAnz + " Vereine"} />
          <DataTable
            value={data}
            size="small"
            selectionMode="single"
            selection={selectedVerein}
            sortField="abk"
            sortOrder={1}
            scrollable
            scrollHeight="35vh"
            onRowHover={this.handleRowHover}
            onSelectionChange={this.handleRowSelect}>
            <Column
              field="abk"
              header="AbkÃ¼rzung"
              style={{
                minWidth: "200px",
              }}
              sortable></Column>
            <Column
              field="name"
              header="Verein"
              style={{
                minWidth: "200px",
              }}
              sortable></Column>

            <Column
              field="strasse"
              header="Strasse"
              style={{
                minWidth: "200px",
              }}></Column>

            <Column
              field="ort"
              header="Ort"
              style={{
                minWidth: "200px",
              }}
              sortable></Column>
          </DataTable>
        </div>

      </div>
    );
  }
}

export default Vereine2;
