import React, { useState, useCallback, useRef } from "react";
import { BtnEditDeleteBack } from "../../services/BtnEditDeleteBack";
import { FormFeld } from "../../services/FormFeld";
import { Toast } from "primereact/toast";
import { Button } from "primereact/button";
import { ConfirmDialog } from "primereact/confirmdialog";

export const VereinFormView2 = ({ onÄndernVerein, btnÄndernVerein, onDeleteVerein, btnLöschenVerein, selectedVerein, onStartMenue }) => {
  const [modusNeuerVerein, setModusNeuerVerein] = useState(false);
  const [setBtnLöschenIsDisabled, setSetBtnLöschenIsDisabled] = useState(true);
  const [setBtnÄndernIsDisabled, setSetBtnÄndernIsDisabled] = useState(true);
  const [setBtnNeuerVereinIsDisabled, setSetBtnNeuerVereinIsDisabled] =
    useState(false);
  const [setVereinFormEditMode, setSetVereinFormEditMode] = useState(true);
  const [visible, setVisible] = useState(false);
  const [vereinsName, setVereinsName] = useState(""); // You need to define vereinsName, accept, and reject variables

  const toast = useRef(null);


  const accept = useCallback(() => {
    const message = vereinsName + " wurde gelöscht!";
    onDeleteVerein();
    toast.current.show({
      severity: "info",
      summary: "Bestätigung",
      detail: message,
      life: 3000,
    });
  });

  const reject = useCallback(() => {
    const message = vereinsName + " wurde nicht gelöscht!";
    toast.current.show({
      severity: "warn",
      summary: "Abbruch",
      detail: message,
      life: 3000,
    });
  });

  const btnNeuerVerein = () => {
    setModusNeuerVerein(true);
    setSetBtnLöschenIsDisabled(true);
    setSetBtnÄndernIsDisabled(true);
    setSetBtnNeuerVereinIsDisabled(false);
    setSetVereinFormEditMode(true);
  };

  const onNeuerVerein = () => {
    btnNeuerVerein();
    // Additional logic you want to perform when "Neuer Verein" button is clicked
  };

  if (selectedVerein) {
    return (
      <>
        <div className="grid m-auto w-11">
          {FormFeld(selectedVerein.name, "Vereinsname", true)}
          {FormFeld(selectedVerein.abk, "Kurzname", true)}
          {FormFeld(selectedVerein.strasse, "Strasse", true)}
          {FormFeld(selectedVerein.plz, "PLZ", true)}
          {FormFeld(selectedVerein.ort, "Ort", true)}
          {FormFeld(selectedVerein.telefon, "Telefon", true)}
          {FormFeld(selectedVerein.bankName, "Bank", true)}
          {FormFeld(selectedVerein.kontoInhaber, "Kontoinhaber", true)}
          {FormFeld(selectedVerein.kiAnschrift, "Kontoinhaber-Adresse", true)}
          {FormFeld(selectedVerein.iban, "IBAN", true)}
          {FormFeld(selectedVerein.bic, "BIC", true)}
        </div>
        <Button
          label="Neuer Verein"
          className="p-button-outlined m-2"
          onClick={onNeuerVerein}
          disabled={btnNeuerVerein}
        />
        <ConfirmDialog
          visible={visible}
          onHide={() => setVisible(false)}
          message={"Soll der " + selectedVerein.name + " gelöscht werden?"}
          header={"Löschen des " + selectedVerein + "?"}
          icon="pi pi-exclamation-triangle"
          accept={accept}
          reject={reject}
        />
        <BtnEditDeleteBack
          onÄndern={onÄndernVerein}
          btnÄndernVerein={btnÄndernVerein}
          setVisible={setVisible}
          btnLöschen={btnLöschenVerein}
          onStartMenue={onStartMenue}
        />
      </>
    );
  }
  return null;
};
