import { useRef } from "react";
import { Toast } from "primereact/toast";

export const MessageSavingEmptyVerein = () => {
  const toast = useRef<Toast>(null);
  console.log("2. leerer Verein");

  const message = " leerer Verein!";

  if (toast.current) {
    toast.current.show({
      severity: "info",
      summary: "Bestätigung",
      detail: message,
      life: 3000,
    });
  }
};
