import React, { useState, useRef, useCallback, useEffect } from "react";
import { Toast } from "primereact/toast";
import { BtnStoreCancel } from "./../../services/BtnStoreCancel";
import { FormFeld } from "../../services/FormFeld";

export function VereinEditForm({
  modusNeuerVerein,
  onSpeichern,
  onAbbruch,
  verein,
}) {
  const toast = useRef(null);

  const createUpdateVerein = useCallback(() => {
    if (name.length === 0 || abk.length === 0) {
      toast.current.show({
        severity: "warn",
        summary: "Achtung",
        detail:
          "mindestens Vereinsname und Kurzbezeichnung m端ssen eingegeben werden!",
        life: 3000,
      });
    } else {
      const neuerVerein = {
        name: name,
        abk: abk,
        strasse: strasse,
        plz: plz,
        ort: ort,
        telefon: telefon,
        bank_name: bankName,
        kontoInhaber: kontoInhaber,
        kiAnschrift: kiAnschrift,
        iban: iban,
        bic: bic,
      };
      console.log(neuerVerein);
      onSpeichern(neuerVerein);
    }
  });

  const [name, setVereinsName] = useState("");
  const [abk, setVereinsKurz] = useState("");
  const [strasse, setVereinsStrasse] = useState("");
  // ... other state values ...

  useEffect(() => {
    if (verein) {
      setVereinsName(verein.name);
      setVereinsKurz(verein.abk);
      setVereinsStrasse(verein.strasse);
      // ... update other state values with their respective properties from verein ...
    }
  }, [verein]);

  return (
    <>
      <Toast ref={toast} />
      <div className="grid m-auto w-11">
        {FormFeld(abk, "Abk端rzung", false, setVereinsKurz)}
        {FormFeld(name, "Vereinsname", false, setVereinsName)}
        {FormFeld(strasse, "Strasse", false, setVereinsStrasse)}
        {/* ... other FormFeld components ... */}
      </div>
      <BtnStoreCancel createUpdate={createUpdateVerein} onAbbruch={onAbbruch} />
    </>
  );
}
