import React, { Component } from "react";
import axios from "axios";
import { MenueHeader } from "../MenueHeader";
import { VereinTable2 } from "../Vereine/VereinTable2";
import { VereinFormView2 } from "../Vereine/VereinFormView2";

class Vereine2 extends Component {
  state = {
    data: [],
    selectedVerein: null,
    loading: true,
    error: null,
    vereinFormEditMode: false,
    modusNeuerVerein: true,
    btnLöschenIsDisabled: true,
    btnÄndernIsDisabled: true,
    btnNeuerVereinIsDisabled: false,
    setModusNeuerVerein: false,
    setBtnLöschenIsDisabled: false,
    setBtnÄndernIsDisabled: false,
    setBtnNeuerVereinIsDisabled: true,
    setVereinFormEditMode: false,
  };

  componentDidMount() {
    this.fetchVereineData();
  }

  fetchVereineData = () => {
    axios
      .get("http://localhost:8080/verein") 
      .then((response) => {
        this.setState({
          data: response.data,
          loading: false,
          error: null,
        });
      })
      .catch((error) => {
        this.setState({
          data: [],
          loading: false,
          error: "An error occurred while fetching data.",
        });
      });
  };

  btnNeuerVerein = () => {
    this.setState({
      modusNeuerVerein: true,
      btnLöschenIsDisabled: true,
      btnÄndernIsDisabled: true,
      btnNeuerVereinIsDisabled: false,
      vereinFormEditMode: true,
    });

    //initVerein(setVerein);
  };

  handleRowHover = (event) => {
    this.setState({ selectedVerein: event.data });
  };

  handleRowSelect = (event) => {
    this.setState({
      vereinFormEditMode: false,
      selectedVerein: event.data,
      btnLöschenIsDisabled: false,
      btnÄndernIsDisabled: false
    });
  };

  deleteVerein = () => {
    // Implement the logic for deleting a Verein here
    // Update state, make API calls, etc.
    this.setState({
      btnLöschenIsDisabled: true,
      btnÄndernIsDisabled: true,
      // Set other state properties as needed
    });
  };



  renderLoadingOrError = () => {
    const { loading, error } = this.state;
    if (loading) {
      return <div>Loading...</div>;
    }
    if (error) {
      return <div>Error: {error}</div>;
    }
    return null;
  };

  btnStartMenue = () => {
    // prüfen, ob die Daten plausibel sind (also nicht leer, Änderung gegenüber den vorherigen, ...)
    window.location.href = "/startmenue";
  };

  render() {
    const { data, selectedVerein } =
      this.state;

    const vereinAnz = data.length;



    return (
      <div>
        <MenueHeader headerText={`${vereinAnz} Vereine`} />
        {this.renderLoadingOrError()}
        <VereinTable2
          data={data}
          selectedVerein={selectedVerein}
          handleRowHover={this.handleRowHover}
          handleRowSelect={this.handleRowSelect}
        />
        <br />
        <VereinFormView2
          onNeuerVerein={this.btnNeuerVerein}
          btnNeuerVerein={this.btnNeuerVereinIsDisabled}
          onÄndernVerein={this.editVerein}
          btnÄndernVerein={this.btnÄndernIsDisabled}
          onDeleteVerein={this.deleteVerein}
          btnLöschenVerein={this.btnLöschenIsDisabled}
          onStartMenue={this.btnStartMenue}
          selectedVerein={selectedVerein}
        />
      </div>
    );
  }
}

export default Vereine2;
